                    if target_o is not None and (0 in target_o.opts or target_o.jumped < t):
                        mid_o = my_opts[x + dx, y + dy]
                        if mid_o.jumped > t and mid_o.opts <= {-sign, -2 * sign}:  # ok if jumped at t
                            if target_o.jumped < t or target_o.opts == {0}:
